mapper.delete:
DELETE FROM focus
WHERE user_id = #{user_id}
        and focus_uid = #{focus_uid};

mapper.select:
SELECT f.*, u.name focusUserName, u.headIcon focusUserIcon
FROM focus f LEFT JOIN user_info u
ON f.focus_uid = u.user_id
WHERE f.user_id = #{user_id}
ORDER BY f.time DESC

    limit #{begin}, #{number};
