mapper.select:
select role from role
where id in (
    select role_id from user_role
    where user_id = #{id}
);

mapper.update:
update user_info
set name=#{name}, sex=#{sex}, address=#{address}
where user_id = #{id};

update user_info
set headIcon=#{headIcon}
where user_id = #{id};

update user_info
set verifyCode = #{verifyCode}, verifyTime = #{verifyTime}
where user_id = #{id};

update user_info
set email=#{email}
where user_id = #{id};

mapper.select:
select *
from user, user_info
where user.id = user_id and user_id = #{id};

mapper.update:
update user
set password = #{password}
where username = #{username};

mapper.insert:
INSERT INTO user_role(user_id, role_id)
VALUES ( #{user_id},
    (SELECT id FROM role
    WHERE role = #{role})
);
